import React from 'react';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
import { Doughnut } from 'react-chartjs-2';


function WinsByWhiteDonut({cumulativeData}) {
    ChartJS.register(ArcElement, Tooltip, Legend);


    const options = {
      plugins: {
        title: {
          display: true,
          text: 'Games as White',
          font: {
            size: 33
          }
        },
        legend: {
          position: 'right'
        }
      }
    }

    const labels = Object.keys(cumulativeData.games_by_color.white)
    let data = {
        labels,
        datasets: [
          {
            label: 'White',
            data: Object.values(cumulativeData.games_by_color.white),
            backgroundColor: [
                'rgb(75, 192, 192)',
                'rgb(255, 99, 132)',
                'rgb(53, 162, 235)'
            ],
            borderColor: [
                'rgb(75, 192, 192)',
                'rgb(255, 99, 132)',
                'rgb(53, 162, 235)'
            ],
            borderWidth: 1,
          },
        ],
      };


    const textCenter = {
      id : "textCenter",
      beforeDatasetsDraw(chart, args, pluginOptions){
        const { ctx, data} = chart;

        ctx.save();
        ctx.font = 'bolder 15px sans-serif';
        ctx.fillStyle = 'red';
        ctx.textAlign = 'center';
        ctx.textBaseline = 'middle';
        ctx.fillText(("White"), chart.getDatasetMeta(0).data[0].x, chart.getDatasetMeta(0).data[0].y);
      }

    }


  return (
    <div>
        <Doughnut options={options} data={data} plugins={[textCenter]} />
    </div>
  )
}

export default WinsByWhiteDonut;
