import React from 'react';
import {
    Chart as ChartJS,
    TimeScale,
    LinearScale,
    LineElement,
    PointElement,
    Title,
    Tooltip,
    Legend,
  } from 'chart.js';
import 'chartjs-adapter-date-fns';
import { Line } from "react-chartjs-2";
import { milliseconds, millisecondsInHour, monthsInQuarter } from 'date-fns';
import {de} from 'date-fns/locale';

ChartJS.register(
    TimeScale,
    LinearScale,
    LineElement,
    PointElement,
    Title,
    Tooltip,
    Legend,
);

function TimeSeriesRating({ cumulativeData }) {

    // console.log(cumulativeData.map((game)=>Object.keys(game)[0]));

    // data must be in this format: [ {timedata in ms since 1970: value},...]

    const data = {
        labels : cumulativeData.map((game)=>Date.parse(Object.keys(game)[0])),
        datasets: [
            {
                label: "label for legend",
                data: cumulativeData.map((game)=>Object.values(game)[0]),
                backgroundColor: 'aqua',
                borderColor: 'black',
                tension: 0.4
            }
        ]
    }

    console.log(data.datasets.data);

    const options = {
        scales: {
            y: {

            },
            x: {
                adapters: {
                    date: {
                        locale: de
                    }
                },
                type: 'time',
                time: {
                    unit: 'millisecond',
                    // format: 'timeFormat'
                },
                // min: cumulativeData[0]
            }
        }
    }






  return (
    <>
        <div>TimeSeriesRating</div>
        <div>
            <Line 
            data={data}
            options={options}/>
        </div>
    </>
  )
}

export default TimeSeriesRating;
