import React from 'react';
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    LineElement,
    PointElement,
    Title,
    Tooltip,
    Legend,
  } from 'chart.js';
import 'chartjs-adapter-date-fns';
import { Line } from "react-chartjs-2";
import { milliseconds } from 'date-fns';

ChartJS.register(
    CategoryScale,
    LinearScale,
    LineElement,
    PointElement,
    Title,
    Tooltip,
    Legend,
);

function TimeSeriesRating({ cumulativeData }) {

    // data must be in this format: [ {timedata in ms since 1970: value},...]

    const data = {
        labels : cumulativeData.map((game)=>Object.keys(game)[0]),
        datasets: [
            {
                label: "label for legend",
                data: cumulativeData.map((game)=>Object.values(game)[0]),
                backgroundColor: 'aqua',
                borderColor: 'black',
                tension: 0.4
            }
        ]
    }

    const options = {
        scales: {
            y: {

            },
            x: {
                type: 'time',
                time: {
                    unit: milliseconds
                }
            }
        }
    }






  return (
    <>
        <div>TimeSeriesRating</div>
        <div>
            <Line 
            data={data}
            options={options}/>
        </div>
    </>
  )
}

export default TimeSeriesRating;
