import React from 'react';
import { Bar } from "react-chartjs-2";
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    BarElement,
    Title,
    Tooltip,
    Legend,
  } from 'chart.js';



function WinsByColorGraph({ chessgames, cumulativeData }) {
    ChartJS.register(
        CategoryScale,
        LinearScale,
        BarElement,
        Title,
        Tooltip,
        Legend
      );


    const options = {
        plugins: {
        title: {
            display: true,
            text: 'Chart.js Bar Chart - Stacked',
        },
        },
        responsive: true,
        interaction: {
        mode: 'index',
        intersect: false
        },
        scales: {
        x: {
            stacked: true,
        },
        y: {
            stacked: true,
        },
        },
    };

    const getStats = (result, obj) => {
        let arr = [];
        let keys = Object.keys(obj);
        keys.forEach((key)=>{
            arr.push(obj[key][result]);
        })
        console.log(arr);
        return arr;
    }

    // const labels = ['January', 'February', 'March', 'April', 'May', 'June', 'July'];
    const labels = Object.keys(cumulativeData.games_by_time);

    const data = {
        labels,
        datasets: [
          {
            label: 'Win',
            data: getStats('win',cumulativeData.games_by_time),
            backgroundColor: 'rgb(75, 192, 192)',
            stack: 'Stack 0',
          },
          {
            label: 'Loss',
            data: getStats('loss',cumulativeData.games_by_time),
            backgroundColor: 'rgb(255, 99, 132)',
            stack: 'Stack 1',
          },
          {
            label: 'Draw',
            data: getStats('draw',cumulativeData.games_by_time),
            backgroundColor: 'rgb(53, 162, 235)',
            stack: 'Stack 2',
          },
        ],
      };
    return (
    <div><Bar options={options} data={data} /></div>
    )
}

export default WinsByColorGraph;
