import React, { useState } from "react";

function SearchBar() {

  const [form, setForm] = useState({
    username: "",
    fromDate: "",
    toDate: ""
  })

  const [chessgames, setChessGames] = useState([])

  const handleChange = (e) => {
    let name = e.target.name;
    let value = e.target.value;

    if (name === 'username') {
        setForm({...form, username: value})
      }
    if (name === 'fromDate') {
      parseInt(value)
      setForm({...form, fromDate: value})
    }
    if (name === 'toDate') {
      parseInt(value)
      setForm({...form, toDate: value})
    }
  }


  // function that takes date (month-year) as input and returns chess data

  async function getChessData(month, year){
    const response = await fetch(`https://api.chess.com/pub/player/${form.username}/games/${year}/${month}`);
    const data = await response.json();
    return data;
  }

  // takes form data as input, and returns all chess data in range (calls getChessData function) 
  async function collectAllData(e){
    e.preventDefault();

    if(form.username === ""){
      alert("Please enter a username.");
    } else if(form.fromDate === "" || form.toDate === ""){
      alert("Please enter a valid date range.")
    }else{
      let fromMonth = (form.fromDate.slice(-2));
      let fromYear = (form.fromDate.slice(0,4));
      let toMonth = (form.toDate.slice(-2));
      let toYear = (form.toDate.slice(0,4));

      // let startDate = new Date(`${fromYear}-${fromMonth}`);
      // let endDate = new Date(`${toYear}-${toMonth}`);
      let allData = [];

      while (fromMonth !== toMonth || fromYear!==toYear){
        let curData = await getChessData(fromMonth, fromYear);
        curData['games'].map((game)=>{
          allData.push(game);
        });
        console.log(fromMonth);
        fromMonth += 1;
      }
      setChessGames({
        data: allData
      });
      // console.log(allData);
    }
    }


  return (
    <div className="searchbar">
      <form>
        <label>Chess.com username: </label>
        <input name="username" type="search" placeholder="Enter username" onChange={(e)=>handleChange(e)}></input>
        &nbsp; &nbsp; 
        <label>From: </label>
        <input name="fromDate" type="month" onChange={(e)=>handleChange(e)}></input>
        &nbsp; &nbsp; 
        <label>To: </label>
        <input name="toDate" type="month" onChange={(e)=>handleChange(e)}></input>
        <button type="submit" onClick={(e)=>collectAllData(e)}>GetData</button>
      </form>

    </div>
  )
}

export default SearchBar
