import React from 'react';
import {
    Chart as ChartJS,
    TimeScale,
    LinearScale,
    LineElement,
    PointElement,
    Title,
    Tooltip,
    Legend,
  } from 'chart.js';
import 'chartjs-adapter-date-fns';
import { Line } from "react-chartjs-2";
import { milliseconds, millisecondsInHour, monthsInQuarter } from 'date-fns';
import {de} from 'date-fns/locale';

ChartJS.register(
    TimeScale,
    LinearScale,
    LineElement,
    PointElement,
    Title,
    Tooltip,
    Legend,
);

function TimeSeriesRating({ cumulativeData }) {

    // console.log(cumulativeData.map((game)=>Object.keys(game)[0]));

    // data must be in this format: [ {timedata in ms since 1970: value},...]


     console.log(`before: ${cumulativeData}`)
    const data = {
        labels : cumulativeData.map((game)=>{
            let strDate = Object.keys(game)[0];
            let intDate = parseInt(strDate)
            let dateDate = new Date(intDate);
            // dateDate.setDate(intDate*1000);
            return intDate;
        }
        ),
        datasets: [
            {
                label: "label for legend",
                data: cumulativeData.map((game)=>Object.values(game)[0]),
                backgroundColor: 'aqua',
                borderColor: 'black',
                // tension: 1
            }
        ]
    }

    console.log(data.labels);

    const options = {
        scales: {
            y: {

            },
            x: {
                adapters: {
                    date: {
                        locale: de
                    }
                },
                type: 'time',
                time: {
                    // unit: 'millisecond',
                    format: 'timeFormat',
                    // round: 'second'
                },
                ticks: {
                    source: 'auto'
                }
            }
        }
    }






  return (
    <>
        <div>TimeSeriesRating</div>
        <div>
            <Line 
            data={data}
            options={options}/>
        </div>
    </>
  )
}

export default TimeSeriesRating;
